$(".holderProgess").mouseover(function(el) {
	move($(el.currentTarget))
 // console.log($(el.target).html());
})


function move(el){

    if(el.hasClass("finished")) return;  
    
    var display = el,
        progressChildBar = el.children(),
        currentValue = parseInt(display.text()),
        nextValue = progressChildBar.attr("data-values"),
        diff = nextValue - currentValue,
        step = (0 < diff ? 1 : -1);
    
    if (nextValue == "0") {
    
        $(display).css("padding", "0");
        
        
    } else {

        
        console.log(nextValue)
        
        progressChildBar.css("color", "#fff").animate({
            "width": nextValue + "%"
        }, "fast");
        
    }

    for (var i = 0; i < Math.abs(diff); ++i) {
    
        setTimeout(function () {
            currentValue += step
            progressChildBar.text(currentValue + "%");
        }, 8 * i);
        
    }

    display.addClass("finished");
}



